  <script>
    const maintenanceLies = document.querySelectorAll(".maintenance_info-block-title li");
    maintenanceLies.forEach((li) => {
        const div = document.createElement("div");
        li.insertBefore(div, li.firstChild);
        li.classList.add("opacity");
    });
  
    document.addEventListener("DOMContentLoaded", function() {
      const maintenanceBanner = document.querySelector(".maintenance_banner");
      const maintenanceBannerH3 = maintenanceBanner.querySelectorAll("h3");
      const maintenanceBannerSlider = maintenanceBanner.querySelector(
        ".maintenance_info-slider"
      );
      const maintenanceBannerLies = maintenanceBanner.querySelectorAll("li");
      const maintenanceBannerP = maintenanceBanner.querySelectorAll("p");
      const maintenanceBannerLinks = maintenanceBanner.querySelectorAll(
        ".maintenance_info-block-social-links a"
      );
  
      function show() {
        setTimeout(() => {
          maintenanceBannerH3.forEach((li, index) => {
            setTimeout(() => {
              li.classList.remove("opacity");
            }, 200 * index);
          });
          setTimeout(() => {
            maintenanceBannerLies.forEach((li, index) => {
              setTimeout(() => {
                li.classList.remove("opacity");
              }, 200 * index);
            });
            setTimeout(() => {
              maintenanceBannerSlider.classList.remove("opacity");
              setTimeout(() => {
                maintenanceBannerP.forEach((li, index) => {
                  setTimeout(() => {
                    li.classList.remove("opacity");
                  }, 200 * index);
                });
                maintenanceBannerLinks.forEach((li, index) => {
                  setTimeout(() => {
                    li.classList.remove("opacity");
                  }, 200 * index);
                });
              }, 300);
            }, 300);
          }, 300);
        }, 500);
      }
  
      // Создаем и запускаем обсервер
      const callback = (entries, observer) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            show();
            return;
          }
        })
      }
  
      let options = {
          threshold: 0.1,
        };
  
      const observer = new IntersectionObserver(callback, options);
      setTimeout(() => {
        observer.observe(maintenanceBanner);
      }, 1000)
    });
  </script>
</div>